import React, { Component } from "react";
import Slider from "react-slick";
import { CopadamEpisodes } from "../Series/CardSerie/CopAdam/CopadamEpisode/CopadamEpisodes";
import { Copadams } from "../Series/CardSerie/CopAdam/copadamm";
import { Carddirilis } from "../Series/CardSerie/Dirilis/Carddirilis/Carddiriliss";



export default class Responsive extends Component {
  render() {
    var settings = {
      dots: true,
      infinite: false,
      speed: 500,
      slidesToShow: 4,
      slidesToScroll: 4,
      initialSlide: 0,
      responsive: [
        {
          breakpoint: 1024,
          settings: {
            slidesToShow: 3,
            slidesToScroll: 3,
            infinite: true,
            dots: true
          }
        },
        {
          breakpoint: 600,
          settings: {
            slidesToShow: 2,
            slidesToScroll: 2,
            initialSlide: 2
          }
        },
        {
          breakpoint: 480,
          settings: {
            slidesToShow: 1,
            slidesToScroll: 1
          }
        }
      ]
    };
    return (
      <div>
        <h3>Best  Series</h3>
        <Slider {...settings}>
          <div>
        <a href="/kurulus"><img src="./series/Kurulus/kuruluso.jpg" alt="kurulus" /> </a>
          </div>
          <div>
          <a href="/dirilis"><img src="./series/Dirilis/dirilise.jpg" alt="dirilis" /> </a>
          </div>
          <div>
          <a href="/dirilis"><img src="./series/Payithabd/payitaht.jpg" alt="abd" /> </a>
          </div>
          <div>
          <a href="/dirilis"><img src="./series/copadam/p.jpg" alt="abd" /> </a>
          </div>
          <div>
            <h3>5</h3>
          </div>
          <div>
            <h3>6</h3>
          </div>
          <div>
            <h3>7</h3>
          </div>
          <div>
            <h3>8</h3>
          </div>
        </Slider>
      </div>
    );
  }
}
